def controller_service_detailed_status(
    admin_socket_path: Path,
    ignore_categories: set[str] | None = None,
) -> DetailedStatus:
    try:
        health = controller_service_health(admin_socket_path)
    except ValueError as e:
        log.error("Failed to parse service health: %s", e)
        return DetailedStatus(Status.ERROR, "Internal error.")
    except Exception:
        return DetailedStatus(Status.ERROR, "Service is not running or starting up")

    status = Status.HEALTHY
    messages = []
    for key in sorted(health.keys()):
        if ignore_categories and key in ignore_categories:
            continue
        value = health[key]
        if not value.get("healthy", False):
            status = Status.WARNING
        if "message" in value:
            messages.append(f'{key}: {value["message"]}')
            # There is a message, convert to WARNING regardless of "healthy" as configd does not want
            # a healthy status with details.
            status = Status.WARNING

    return DetailedStatus(status, "; ".join(messages) if messages else None)

